#include <iostream>
#include <string>
#include <sstream>

using namespace std;

long s_to_i(char s) {
    switch (s) {
        case '0':
            return 0;
        case '1':
            return 1;
        case '2':
            return 2;
        case '3':
            return 3;
        case '4':
            return 4;
        case '5':
            return 5;
        case '6':
            return 6;
        case '7':
            return 7;
        case '8':
            return 8;
        case '9':
            return 9;
        default:
            return -1;
    }
}


int main() {
    string s = "";
    int i, j, N = 13;
    long p, m = 0;
    string tmp = "", ms = "";
    for (i = 0; i < s.length() - N; i++) {
        p = 1;
        tmp = "";
        for (j = 0; j < N; j++) {
            p = p * s_to_i(s[i+j]);
            tmp += s[i+j];
        }
        cout << m << " " << ms << " " << tmp << " " << p <<endl;
        if (p > m) {
            m = p;
            ms = "";
            for (j = 0; j < tmp.length(); j++) {
                ms += tmp[j];
            }
        }
    }
    cout << ms << endl;
    return 0;
}

